mapper.select:
select * from address_book


        and user_id = #{userId}


        and consignee like concat('%',#{consignee},'%')


        and phone = #{phone}


        and is_default = #{isDefault}


order by is_default desc

mapper.update:
update address_book
consignee = #{consignee}, phone = #{phone}, sex = #{sex}, province_code = #{provinceCode}, province_name = #{provinceName}, city_code = #{cityCode}, city_name = #{cityName}, district_code = #{districtCode}, district_name = #{districtName}, detail = #{detail}, label = #{label}, is_default = #{isDefault}
where id = #{id}

update address_book
set is_default = #{isDefault}
where user_id = #{userId}
